  ArrowRight, 
  Sparkles,
  Link as LinkIcon,
  FileText,
  Clock
} from "lucide-react";
import { StatusBadge, type BillStatus } from "./StatusBadge";

interface RelatedBill {
  id: string;
  billNumber: string;
  title: string;
  status: BillStatus;
  relationship: "amends" | "complements" | "conflicts" | "supersedes" | "predecessor";
  summary: string;
}

interface RelatedBillsProps {
  currentBillId: string;
  bills?: RelatedBill[];
  onAnalyze?: () => void;
}

export const mockRelatedBills: RelatedBill[] = [
  {
    id: "30005",
    billNumber: "ZTA-24-08",
    title: "Transit-Oriented Development Standards",
    status: "enacted",
    relationship: "complements",
    summary: "Sets development standards for areas near transit hubs, which this corridor plan builds upon."
  },
  {
    id: "30006",
    billNumber: "ZTA-23-15",
    title: "Accessory Dwelling Unit Expansion",
    status: "passed",
    relationship: "predecessor",
    summary: "Earlier legislation that allowed ADUs county-wide; this bill extends similar principles to multi-family."
  },
  {
    id: "30007",
    billNumber: "HB-25-03",
    title: "Affordable Housing Trust Fund Increase",
    status: "in_committee",
    relationship: "complements",
    summary: "Would increase funding for affordable housing programs that could support units created by this rezoning."
  },
];

const relationshipLabels = {
  amends: { label: "Amends", color: "bg-amber-100 text-amber-700 dark:bg-amber-900/30 dark:text-amber-400" },
  complements: { label: "Complements", color: "bg-emerald-100 text-emerald-700 dark:bg-emerald-900/30 dark:text-emerald-400" },
  conflicts: { label: "May Conflict", color: "bg-red-100 text-red-700 dark:bg-red-900/30 dark:text-red-400" },
  supersedes: { label: "Supersedes", color: "bg-blue-100 text-blue-700 dark:bg-blue-900/30 dark:text-blue-400" },
  predecessor: { label: "Predecessor", color: "bg-slate-100 text-slate-700 dark:bg-slate-800 dark:text-slate-300" },
};

export function RelatedBills({ currentBillId, bills = mockRelatedBills, onAnalyze }: RelatedBillsProps) {
  const [showAnalysis, setShowAnalysis] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  const handleAnalyze = () => {
    setIsAnalyzing(true);
    setTimeout(() => {
      setIsAnalyzing(false);
      setShowAnalysis(true);
      onAnalyze?.();
    }, 1500);
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between gap-4 flex-wrap">
          <CardTitle className="flex items-center gap-2 text-xl">
            <GitBranch className="h-5 w-5 text-primary" />
            Related Legislation
          </CardTitle>
          <Button 
            variant="outline" 
            size="sm" 
            onClick={handleAnalyze}
            disabled={isAnalyzing}
            data-testid="button-analyze-relations"
          >
            <Sparkles className="h-4 w-4 mr-2" />
            {isAnalyzing ? "Analyzing..." : "Analyze Connections"}
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        {showAnalysis && (
          <div className="mb-6 p-4 rounded-lg" style={{ backgroundColor: "hsl(var(--sage-light))" }}>
            <h4 className="font-medium text-foreground mb-2 flex items-center gap-2">
              <Sparkles className="h-4 w-4" style={{ color: "hsl(var(--sage))" }} />
              Connection Analysis
            </h4>
            <p className="text-sm text-muted-foreground leading-relaxed">
              This bill is part of a broader effort to increase housing density near transit. 
              It builds on ZTA-24-08 which established baseline standards, and aligns with pending 
              affordable housing funding in HB-25-03. Together, these bills form a coordinated 
              approach to addressing the county's housing shortage while maintaining neighborhood character.
            </p>
            <p className="text-xs text-muted-foreground mt-3 flex items-center gap-1">
              <Clock className="h-3 w-3" />
              Analysis generated Dec 15, 2025
            </p>
          </div>
        )}

        <div className="space-y-3">
          {bills.map((bill) => (
            <Link key={bill.id} href={`/bill/${bill.id}`}>
              <div 
                className="p-4 rounded-lg border hover-elevate cursor-pointer"
                data-testid={`related-bill-${bill.id}`}
              >
                <div className="flex items-start justify-between gap-3 mb-2">
                  <div className="flex items-center gap-2 flex-wrap">
                    <span className="font-mono text-sm text-muted-foreground">{bill.billNumber}</span>
                    <StatusBadge status={bill.status} />
                    <Badge className={relationshipLabels[bill.relationship].color}>
                      <LinkIcon className="h-3 w-3 mr-1" />
                      {relationshipLabels[bill.relationship].label}
                    </Badge>
                  </div>
                  <ArrowRight className="h-4 w-4 text-muted-foreground shrink-0" />
                </div>
                <h4 className="font-medium text-foreground mb-1">{bill.title}</h4>
                <p className="text-sm text-muted-foreground line-clamp-2">{bill.summary}</p>
              </div>
            </Link>
          ))}
        </div>

        <div className="mt-4 pt-4 border-t">
          <div className="flex items-center gap-2 text-xs text-muted-foreground">
            <FileText className="h-3 w-3" />
            <span>Relationships identified by legislative analysis</span>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/ImpactAnalyzer.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { 
  Sparkles, 
  MapPin, 
  User,
  Home,
  Building2,
  Briefcase,
  AlertTriangle,
  Check,
  TrendingUp,
  TrendingDown,
  Clock
} from "lucide-react";

interface ImpactResult {
  affected: boolean;
  impacts: {
    category: string;
    description: string;
    type: "positive" | "negative" | "neutral";
  }[];
  summary: string;
}

interface ImpactAnalyzerProps {
  billId: string;
  billTitle: string;
}

const entityTypes = [
  { value: "homeowner", label: "Homeowner", icon: Home },
  { value: "renter", label: "Renter", icon: Building2 },
  { value: "business", label: "Business Owner", icon: Briefcase },
  { value: "commuter", label: "Commuter", icon: User },
];

export function ImpactAnalyzer({ billId, billTitle }: ImpactAnalyzerProps) {
  const [address, setAddress] = useState("");
  const [entityType, setEntityType] = useState<string>("");
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [result, setResult] = useState<ImpactResult | null>(null);
  const [showForm, setShowForm] = useState(false);

  const handleAnalyze = () => {
    if (!address.trim() || !entityType) return;

    setIsAnalyzing(true);

    setTimeout(() => {
      setResult({
        affected: true,
        summary: `Based on your location and status as a ${entityType}, this bill will likely affect you in the following ways:`,
        impacts: [
          {
            category: "Property",
            description: entityType === "homeowner" 
              ? "Your property may be eligible for conversion to duplex/triplex, potentially increasing value by 10-20%"
              : "More housing options may become available in your area within 2-3 years",
            type: "positive"
          },
          {
            category: "Transportation",
            description: "New bike lanes and improved bus stops will be installed within 0.5 miles of your address",
            type: "positive"
          },
          {
            category: "Taxes",
            description: entityType === "homeowner"
              ? "Property taxes may increase slightly ($50-150/year) due to infrastructure improvements"
              : "No direct tax impact expected",
            type: entityType === "homeowner" ? "negative" : "neutral"
          },
          {
            category: "Traffic",
            description: "Short-term construction disruptions expected for 6-12 months",
            type: "negative"
          },
        ]
      });
      setIsAnalyzing(false);
    }, 2000);
  };

  const handleReset = () => {
    setResult(null);
    setAddress("");
    setEntityType("");
    setShowForm(false);
  };

  if (!showForm && !result) {
    return (
      <Card className="border-2 border-dashed" style={{ borderColor: "hsl(var(--sage) / 0.4)" }}>
        <CardContent className="p-8 text-center">
          <div 
            className="inline-flex items-center justify-center p-4 rounded-full mb-4"
            style={{ backgroundColor: "hsl(var(--sage-light))" }}
          >
            <Sparkles className="h-8 w-8" style={{ color: "hsl(var(--sage))" }} />
          </div>
          <h3 className="text-xl font-medium text-foreground mb-2">
            See How This Affects You
          </h3>
          <p className="text-muted-foreground mb-6 max-w-md mx-auto">
            Get a personalized analysis of how this legislation impacts you based on your location and situation.
          </p>
          <Button 
            onClick={() => setShowForm(true)}
            style={{ backgroundColor: "hsl(var(--sage))" }}
            className="text-white"
            data-testid="button-start-impact-analysis"
          >
            <MapPin className="h-4 w-4 mr-2" />
            Analyze My Impact
          </Button>
        </CardContent>
      </Card>
    );
  }

  if (result) {
    return (
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between gap-4 flex-wrap">
            <CardTitle className="flex items-center gap-2 text-xl">
              <Sparkles className="h-5 w-5" style={{ color: "hsl(var(--sage))" }} />
              Your Impact Analysis
            </CardTitle>
            <Button variant="outline" size="sm" onClick={handleReset}>
              Analyze Different Address
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="flex items-center gap-2 mb-4 text-sm text-muted-foreground">
            <MapPin className="h-4 w-4" />
            <span>{address}</span>
            <Badge variant="outline" className="ml-2">
              {entityTypes.find(e => e.value === entityType)?.label}
            </Badge>
          </div>

          <div 
            className="p-4 rounded-lg mb-6"
            style={{ backgroundColor: result.affected ? "hsl(var(--sage-light))" : "hsl(var(--muted))" }}
          >
            <div className="flex items-center gap-2 mb-2">
              {result.affected ? (
                <AlertTriangle className="h-5 w-5" style={{ color: "hsl(var(--sage))" }} />
              ) : (
                <Check className="h-5 w-5 text-muted-foreground" />
              )}
              <span className="font-medium text-foreground">
                {result.affected ? "This bill affects you" : "Limited direct impact"}
              </span>
            </div>
            <p className="text-sm text-muted-foreground">{result.summary}</p>
          </div>

          <div className="space-y-3">
            {result.impacts.map((impact, i) => (
              <div 
                key={i} 
                className="flex items-start gap-3 p-3 rounded-lg bg-muted/50"
                data-testid={`impact-${i}`}
              >
                <div className="shrink-0 mt-0.5">
                  {impact.type === "positive" && (
                    <TrendingUp className="h-5 w-5 text-primary" />
                  )}
                  {impact.type === "negative" && (
                    <TrendingDown className="h-5 w-5 text-destructive" />
                  )}
                  {impact.type === "neutral" && (
                    <div className="h-5 w-5 rounded-full bg-muted-foreground/20" />
                  )}
                </div>
                <div className="flex-1">
                  <div className="font-medium text-foreground text-sm mb-0.5">
                    {impact.category}
                  </div>
                  <p className="text-sm text-muted-foreground">{impact.description}</p>
                </div>
              </div>
            ))}
          </div>

          <div className="mt-4 pt-4 border-t flex items-center gap-2 text-xs text-muted-foreground">
            <Clock className="h-3 w-3" />
            <span>Analysis based on bill text and geographic data. Results are estimates.</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2 text-xl">
          <Sparkles className="h-5 w-5" style={{ color: "hsl(var(--sage))" }} />
          See How This Affects You
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          <div>
            <label className="text-sm font-medium text-foreground mb-2 block">
              Your Address
            </label>
            <div className="relative">
              <MapPin className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Enter your street address..."
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                className="pl-9"
                data-testid="input-impact-address"
              />
            </div>
          </div>

          <div>
            <label className="text-sm font-medium text-foreground mb-2 block">
              I am a...
            </label>
            <Select value={entityType} onValueChange={setEntityType}>
              <SelectTrigger data-testid="select-entity-type">
                <SelectValue placeholder="Select your situation" />
              </SelectTrigger>
              <SelectContent>
                {entityTypes.map((type) => (
                  <SelectItem key={type.value} value={type.value}>
                    <div className="flex items-center gap-2">
                      <type.icon className="h-4 w-4" />
                      {type.label}
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="flex gap-2">
            <Button 
              onClick={handleAnalyze}
              disabled={!address.trim() || !entityType || isAnalyzing}
              className="flex-1"
              style={{ backgroundColor: "hsl(var(--sage))" }}
              data-testid="button-run-analysis"
            >
              {isAnalyzing ? (
                <>Analyzing...</>
              ) : (
                <>
                  <Sparkles className="h-4 w-4 mr-2" />
                  Analyze Impact
                </>
              )}
            </Button>
            <Button variant="outline" onClick={handleReset}>
              Cancel
            </Button>
          </div>

          <p className="text-xs text-muted-foreground text-center">
            Your address is not stored. Analysis happens in your browser.
          </p>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/CommentSection.tsx.content:
import { useState, useRef } from "react";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { MessageSquare, Send, ArrowUp, Reply, MapPin, Shield } from "lucide-react";

const MAX_COMMENT_LENGTH = 1000;
const RATE_LIMIT_MS = 30000;
const BLOCKED_WORDS = ["spam", "scam", "idiot", "stupid", "moron", "dumb"];

function containsBlockedWords(text: string): boolean {
  const lowerText = text.toLowerCase();
  return BLOCKED_WORDS.some(word => lowerText.includes(word));
}

export interface Comment {
  id: string;
  author: string;
  authorInitials: string;
  neighborhood: string;
  content: string;
  timestamp: string;
  upvotes: number;
  isModerator?: boolean;
  replies?: Comment[];
}

interface CommentSectionProps {
  comments: Comment[];
  onAddComment: (content: string) => void;
  onUpvote: (commentId: string) => void;
  isLoggedIn: boolean;
}

function CommentItem({ 
  comment, 
  depth = 0, 
  onUpvote, 
  isLoggedIn 
}: { 
  comment: Comment; 
  depth?: number; 
  onUpvote: (id: string) => void;
  isLoggedIn: boolean;
}) {
  const [showReply, setShowReply] = useState(false);
  const [upvoted, setUpvoted] = useState(false);
  const [upvoteCount, setUpvoteCount] = useState(comment.upvotes);

  const handleUpvote = () => {
    if (!isLoggedIn) return;
    setUpvoted(!upvoted);
    setUpvoteCount((prev) => (upvoted ? prev - 1 : prev + 1));
    onUpvote(comment.id);
  };

  return (
    <div className={`${depth > 0 ? "ml-8 pl-4 border-l-2 border-border" : ""}`}>
      <div className="py-4" data-testid={`comment-${comment.id}`}>
        <div className="flex gap-3">
          <Avatar className="h-11 w-11 shrink-0">
            <AvatarFallback className="text-sm font-medium">{comment.authorInitials}</AvatarFallback>
          </Avatar>
          <div className="flex-1 min-w-0">
            <div className="flex flex-wrap items-center gap-2 mb-2">
              <span className="font-medium text-lg text-foreground" data-testid={`text-comment-author-${comment.id}`}>
                {comment.author}
              </span>
              {comment.isModerator && (
                <Badge variant="outline" className="text-xs gap-1 bg-primary/10 text-primary border-primary/30">
                  <Shield className="h-3 w-3" />
                  Moderator
                </Badge>
              )}
              <Badge variant="outline" className="text-xs gap-1" data-testid={`badge-neighborhood-${comment.id}`}>
                <MapPin className="h-3 w-3" />
                {comment.neighborhood}
              </Badge>
              <span className="text-sm text-muted-foreground" data-testid={`text-comment-time-${comment.id}`}>
                {comment.timestamp}
              </span>
            </div>
            <p className="text-lg text-foreground leading-relaxed mb-3" data-testid={`text-comment-content-${comment.id}`}>
              {comment.content}
            </p>
            <div className="flex items-center gap-4">
              <Button
                variant="ghost"
                size="sm"
                onClick={handleUpvote}
                disabled={!isLoggedIn}
                className={`text-base ${upvoted ? "text-accent" : "text-muted-foreground"}`}
                data-testid={`button-upvote-${comment.id}`}
              >
                <ArrowUp className={`h-5 w-5 mr-1 ${upvoted ? "fill-current" : ""}`} />
                {upvoteCount}
              </Button>
              {isLoggedIn && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setShowReply(!showReply)}
                  className="text-muted-foreground text-base"
                  data-testid={`button-reply-${comment.id}`}
                >
                  <Reply className="h-4 w-4 mr-1" />
                  Reply
                </Button>
              )}
            </div>
          </div>
        </div>

        {showReply && (
          <div className="mt-4 ml-14">
            <Textarea
              placeholder="Write a reply..."
              className="mb-2 text-base min-h-20"
              data-testid={`input-reply-${comment.id}`}
            />
            <div className="flex gap-2">
              <Button size="sm" data-testid={`button-submit-reply-${comment.id}`}>
                <Send className="h-4 w-4 mr-1" />
                Reply
              </Button>
              <Button variant="ghost" size="sm" onClick={() => setShowReply(false)}>
                Cancel
              </Button>
            </div>
          </div>
        )}
      </div>

      {comment.replies?.map((reply) => (
        <CommentItem 
          key={reply.id} 
          comment={reply} 
          depth={depth + 1} 
          onUpvote={onUpvote}
          isLoggedIn={isLoggedIn}
        />
      ))}
    </div>
  );
}

export function CommentSection({ comments, onAddComment, onUpvote, isLoggedIn }: CommentSectionProps) {
  const [newComment, setNewComment] = useState("");
  const [error, setError] = useState("");
  const lastSubmitRef = useRef<number>(0);

  const handleCommentChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const value = e.target.value;
    if (value.length <= MAX_COMMENT_LENGTH) {
      setNewComment(value);
      setError("");
    }
  };

  const handleSubmit = () => {
    const trimmed = newComment.trim();
    if (!trimmed) return;

    if (containsBlockedWords(trimmed)) {
      setError("Please keep comments respectful and constructive.");
      return;
    }

    const now = Date.now();
    if (now - lastSubmitRef.current < RATE_LIMIT_MS) {
      const secondsLeft = Math.ceil((RATE_LIMIT_MS - (now - lastSubmitRef.current)) / 1000);
      setError(`Please wait ${secondsLeft} seconds before posting again.`);
      return;
    }

    lastSubmitRef.current = now;
    onAddComment(trimmed);
    setNewComment("");
    setError("");
  };

  const charsRemaining = MAX_COMMENT_LENGTH - newComment.length;

  return (
    <div>
      <div className="flex items-center gap-2 mb-6">
        <MessageSquare className="h-6 w-6 text-muted-foreground" />
        <h3 className="text-2xl font-medium text-foreground">
          Community Discussion ({comments.length})
        </h3>
      </div>

      {isLoggedIn && (
        <div className="mb-6">
          <Textarea
            placeholder="Share your thoughts on this legislation..."
            value={newComment}
            onChange={handleCommentChange}
            maxLength={MAX_COMMENT_LENGTH}
            className="mb-2 text-lg min-h-28"
            data-testid="input-new-comment"
            aria-describedby="comment-char-count"
          />
          <div className="flex items-center justify-between mb-3">
            <span 
              id="comment-char-count"
              className={`text-sm ${charsRemaining < 100 ? "text-destructive" : "text-muted-foreground"}`}
            >
              {charsRemaining} characters remaining
            </span>
            {error && <span className="text-sm text-destructive">{error}</span>}
          </div>
          <Button onClick={handleSubmit} size="lg" disabled={!newComment.trim()} data-testid="button-submit-comment">
            <Send className="h-4 w-4 mr-2" />
            Post Comment
          </Button>
        </div>
      )}

      <div className="divide-y divide-border">
        {comments.length === 0 ? (
          <p className="py-8 text-center text-lg text-muted-foreground">
            No comments yet. Be the first to share your thoughts!
          </p>
        ) : (
          comments.map((comment) => (
            <CommentItem 
              key={comment.id} 
              comment={comment} 
              onUpvote={onUpvote}
              isLoggedIn={isLoggedIn}
            />
          ))
        )}
      </div>
    </div>
  );
}

