client/src/components/DataSourceBadge.ts.content:
import { Badge } from "@/components/ui/badge";
import { 
  CheckCircle2, 
  Database, 
  ExternalLink,
  AlertCircle 
} from "lucide-react";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";

export type DataSourceType = 'live' | 'official' | 'sample' | 'curated';

interface DataSourceBadgeProps {
  type: DataSourceType;
  sourceName?: string;
  sourceUrl?: string;
  showTooltip?: boolean;
}

const badgeConfig: Record<DataSourceType, {
  label: string;
  icon: typeof CheckCircle2;
  className: string;
  tooltip: string;
}> = {
  live: {
    label: 'Live Data',
    icon: CheckCircle2,
    className: 'bg-green-500/10 text-green-700 dark:text-green-400 border-green-300 dark:border-green-700',
    tooltip: 'This data is fetched directly from official government APIs in real-time.',
  },
  official: {
    label: 'Official',
    icon: Database,
    className: 'bg-blue-500/10 text-blue-700 dark:text-blue-400 border-blue-300 dark:border-blue-700',
    tooltip: 'This data comes from official government records.',
  },
  sample: {
    label: 'Sample Data',
    icon: AlertCircle,
    className: 'bg-yellow-500/10 text-yellow-700 dark:text-yellow-400 border-yellow-300 dark:border-yellow-700',
    tooltip: 'This is illustrative sample data for demonstration purposes.',
  },
  curated: {
    label: 'Curated',
    icon: CheckCircle2,
    className: 'bg-purple-500/10 text-purple-700 dark:text-purple-400 border-purple-300 dark:border-purple-700',
    tooltip: 'This data is manually curated from official sources.',
  },
};

export function DataSourceBadge({ 
  type, 
  sourceName, 
  sourceUrl,
  showTooltip = true 
}: DataSourceBadgeProps) {
  const config = badgeConfig[type];
  const Icon = config.icon;

  const badge = (
    <Badge 
      variant="outline" 
      className={`text-xs gap-1 ${config.className}`}
      data-testid={`badge-data-source-${type}`}
    >
      <Icon className="h-3 w-3" />
      {config.label}
      {sourceUrl && (
        <a 
          href={sourceUrl} 
          target="_blank" 
          rel="noopener noreferrer"
          onClick={(e) => e.stopPropagation()}
          className="ml-0.5"
        >
          <ExternalLink className="h-3 w-3" />
        </a>
      )}
    </Badge>
  );

  if (!showTooltip) {
    return badge;
  }

  return (
    <Tooltip>
      <TooltipTrigger asChild>
        {badge}
      </TooltipTrigger>
      <TooltipContent className="max-w-xs">
        <p className="text-sm">{config.tooltip}</p>
        {sourceName && (
          <p className="text-xs text-muted-foreground mt-1">
            Source: {sourceName}
          </p>
        )}
      </TooltipContent>
    </Tooltip>
  );
}

// Component for showing data source info in a more detailed format
interface DataSourceInfoProps {
  sources: {
    name: string;
    type: DataSourceType;
    url?: string;
    note?: string;
  }[];
}

export function DataSourceInfo({ sources }: DataSourceInfoProps) {
  return (
    <div className="text-xs text-muted-foreground space-y-1 p-3 bg-muted/30 rounded-md">
      <p className="font-medium text-foreground mb-2">Data Sources:</p>
      {sources.map((source, index) => (
        <div key={index} className="flex items-start gap-2">
          <DataSourceBadge type={source.type} showTooltip={false} />
          <div>
            {source.url ? (
              <a 
                href={source.url} 
                target="_blank" 
                rel="noopener noreferrer"
                className="text-primary hover:underline inline-flex items-center gap-1"
              >
                {source.name}
                <ExternalLink className="h-3 w-3" />
              </a>
            ) : (
              <span>{source.name}</span>
            )}
            {source.note && (
              <p className="text-muted-foreground mt-0.5">{source.note}</p>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}

client/src/components/BillAmendments.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from "@/components/ui/collapsible";
import { ChevronDown, FileText, Check, X, ExternalLink } from "lucide-react";
import { useState } from "react";
import { Button } from "@/components/ui/button";

export interface Amendment {
  id: number;
  amendmentNumber: string;
  title: string;
  summary: string | null;
  sponsorName: string | null;
  status: string;
  voteDate: string | null;
  yesVotes: number | null;
  noVotes: number | null;
  sourceUrl: string | null;
}

interface BillAmendmentsProps {
  amendments: Amendment[];
}

export function BillAmendments({ amendments }: BillAmendmentsProps) {
  const [openItems, setOpenItems] = useState<Set<number>>(new Set());

  const toggleItem = (id: number) => {
    setOpenItems((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(id)) {
        newSet.delete(id);
      } else {
        newSet.add(id);
      }
      return newSet;
    });
  };

  const getStatusBadge = (status: string) => {
    switch (status.toLowerCase()) {
      case "passed":
      case "adopted":
        return (
          <Badge variant="outline" className="bg-chart-1/10 text-chart-1 border-chart-1/30 gap-1">
            <Check className="h-3 w-3" />
            Passed
          </Badge>
        );
      case "failed":
      case "rejected":
        return (
          <Badge variant="outline" className="bg-destructive/10 text-destructive border-destructive/30 gap-1">
            <X className="h-3 w-3" />
            Failed
          </Badge>
        );
      case "pending":
        return (
          <Badge variant="outline" className="bg-amber-500/10 text-amber-600 border-amber-500/30">
            Pending
          </Badge>
        );
      default:
        return (
          <Badge variant="outline" className="bg-muted text-muted-foreground">
            {status}
          </Badge>
        );
    }
  };

  if (amendments.length === 0) {
    return null;
  }

  return (
    <Card>
      <CardContent className="p-6">
        <div className="flex items-center gap-2 mb-6">
          <FileText className="h-6 w-6 text-muted-foreground" />
          <h3 className="text-xl font-medium text-foreground">
            Amendments ({amendments.length})
          </h3>
        </div>

        <div className="space-y-3">
          {amendments.map((amendment) => (
            <Collapsible
              key={amendment.id}
              open={openItems.has(amendment.id)}
              onOpenChange={() => toggleItem(amendment.id)}
            >
              <div className="border border-border rounded-lg overflow-hidden">
                <CollapsibleTrigger className="w-full" data-testid={`button-amendment-${amendment.id}`}>
                  <div className="flex items-center justify-between gap-4 p-4 hover-elevate cursor-pointer">
                    <div className="flex items-center gap-3 flex-1 min-w-0">
                      <span className="font-medium text-foreground shrink-0">
                        {amendment.amendmentNumber}
                      </span>
                      <span className="text-muted-foreground truncate text-left">
                        {amendment.title}
                      </span>
                    </div>
                    <div className="flex items-center gap-3 shrink-0">
                      {getStatusBadge(amendment.status)}
                      <ChevronDown
                        className={`h-5 w-5 text-muted-foreground transition-transform ${
                          openItems.has(amendment.id) ? "rotate-180" : ""
                        }`}
                      />
                    </div>
                  </div>
                </CollapsibleTrigger>

                <CollapsibleContent>
                  <div className="px-4 pb-4 pt-0 border-t border-border">
                    <div className="pt-4 space-y-3">
                      {amendment.summary && (
                        <div>
                          <p className="text-sm font-medium text-muted-foreground mb-1">Summary</p>
                          <p className="text-foreground">{amendment.summary}</p>
                        </div>
                      )}

                      <div className="flex flex-wrap gap-x-6 gap-y-2 text-sm">
                        {amendment.sponsorName && (
                          <div>
                            <span className="text-muted-foreground">Sponsor: </span>
                            <span className="text-foreground font-medium">{amendment.sponsorName}</span>
                          </div>
                        )}
                        {amendment.voteDate && (
                          <div>
                            <span className="text-muted-foreground">Vote Date: </span>
                            <span className="text-foreground">{amendment.voteDate}</span>
                          </div>
                        )}
                        {amendment.yesVotes !== null && amendment.noVotes !== null && (
                          <div>
                            <span className="text-muted-foreground">Vote: </span>
                            <span className="text-chart-1 font-medium">{amendment.yesVotes} Yes</span>
                            <span className="text-muted-foreground"> / </span>
                            <span className="text-destructive font-medium">{amendment.noVotes} No</span>
                          </div>
                        )}
                      </div>

                      {amendment.sourceUrl && (
                        <div className="pt-2">
                          <a
                            href={amendment.sourceUrl}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="inline-flex items-center gap-1 text-sm text-primary hover:underline"
                            data-testid={`link-amendment-source-${amendment.id}`}
                          >
                            <ExternalLink className="h-3.5 w-3.5" />
                            View Full Amendment
                          </a>
                        </div>
                      )}
                    </div>
                  </div>
                </CollapsibleContent>
              </div>
            </Collapsible>
          ))}
        </div>
      </CardContent>
    </Card>
  );
}

